mapper.select:
select count(*) as activityNum
from activity
where organizer_id = #{organizerId} and status = #{activityStatus}

select count(*) as activityNum
from activity
where name = #{activityName} and theme = #{activityTheme}

mapper.update:
update activity
set status = #{activityStatus}
where id = #{activityId}

update activity
set volunteer_current_number = #{activityVolunteerNum}
where id = #{activityId}
